import './SearchButtonStyle.css';
import { movieGenres } from './MovieGenres.js';
import axios from 'axios';
// image url prefix: https://image.tmdb.org/t/p/w500//
function SearchButton() {
    function GetMovieType(movieName) {
        try{
        let movieId = movieGenres.find(o => o.name === movieName);
        axios.get(`https://api.themoviedb.org/3/discover/movie?api_key=f76697f079ac4cc9cba2d8f4db3a80d4&with_genres=${movieId.id}`)
        .then((response) => {
        console.log(response.data);
        populateContainer(response.data);
        // console.log(response.status);
        // console.log(response.statusText);
        // console.log(response.headers);
        // console.log(response.config);
    });
        }catch(e){
            alert("No Movies found");
        }
      }
  return (
        <button id="buttonstyle"type="button"  onClick={()=> GetMovieType(document.getElementById("inputstyle").value)}>Click Me!</button>
  );
}

function populateContainer(populateRows){

    const firstcontainermovies = ReactDOM.createRoot(document.getElementById('firstcontainermovies'));
    firstcontainermovies.render(
      <React.StrictMode>    
        <FirstContainerMovies />
      </React.StrictMode>
    );

    <div className="flex-container">
    <ComponentRow/>
    <hr/>
    <ComponentRow/>
  
   </div>
  


}

export default SearchButton;
