import './SearchButtonStyle.css';
import { movieGenres } from './MovieGenres.js';
import {fetch as fetchPolyfill} from 'whatwg-fetch'

function SearchButton() {
    function GetMovieType(movieName) {
        try{
        let movieId = movieGenres.find(o => o.name === movieName);
        const getMoviesFromApi = () => {
            return fetch(`https://mywebsite.com/https://api.themoviedb.org/3/discover/movie?api_key=f76697f079ac4cc9cba2d8f4db3a80d4&with_genres=${movieId.id}/`, {
            method: 'GET',
            headers: {
              'Accept': 'application/json',
              'Content-Type': 'application/json',
            }
          })
        }

        }catch(e){

        }
      }
  return (
        <button id="buttonstyle"type="button"  onClick={()=> GetMovieType(document.getElementById("inputstyle").value)}>Click Me!</button>
  );
}


export default SearchButton;
