import "./SearchButtonStyle.css";
import { movieGenres } from "./MovieGenres.js";
import axios from "axios";
import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';

// image url prefix: https://image.tmdb.org/t/p/w500//
function SearchButton() {
  function GetMovieType(movieName) {
    try {
      let movieId = movieGenres.find((o) => o.name === movieName);
      axios
        .get(
          `https://api.themoviedb.org/3/discover/movie?api_key=f76697f079ac4cc9cba2d8f4db3a80d4&with_genres=${movieId.id}`
        )
        .then((response) => {
          console.log(response.data);
          populateContainer(response.data.results);
        });
    } catch (e) {
      alert("No Movies found");
    }
  }

  function populateContainer(populateRows) {
    const firstcontainermovies = ReactDOM.createRoot(
      document.getElementById("firstcontainermovies")
    );
    return (
      <div className="flex-container">
        <ComponentRow />
        <hr />
        <ComponentRow />
      </div>
    );
    for (var i = 0; i < populateRows.length; i++) {
      console.log(populateRows[i]);
    }
  }
  return (
    <button
      id="buttonstyle"
      type="button"
      onClick={() => GetMovieType(document.getElementById("inputstyle").value)}
    >
      Click Me!
    </button>
  );
}

export default SearchButton;
